var express = require("express");
var app = express();
var client = require(".");
app.use("/",require("srnet-server")());
app.listen(()=>{
  var cli = new client("https://srnet-client.theforarkld.repl.co/");
  cli.on("connect",id=>{
    cli.send("0000111",id);
  });
  cli.on("data",(d)=>{
    console.log(d.message);
    if(d.type == "ASK") d.return("yo");
  });
  cli.connect();
});