var express = require("express");
var app = express();
var server = require(".")();
app.use(server);
app.listen(()=>{
  var srnet = require("srnet").client("https://srnet-server-but-better.theforarkld.repl.co");
  var od;
  srnet.on("connect",()=>{
    od = new Date();
    srnet.send("0000110","yo");
  });
  srnet.on("data",d=>{
    if(d.statusText == "DATE/TIME"){
      console.log(new Date() - od);
    }
  });
  srnet.on("allRawData",d=>{
    process.stdout.write(d);
  });
});